(4034, 202, 'θυμιατήριον', '27.225', 'θυμιατήριον', 'thymiatèrion', 'encensoir', 'censer, thurible', 'μετὰ τοῦ θυμιατηρίου ἐπισφραγίζοντος', 'Encensement réalisé avec un thymatèrion.', 37, 0, 0, 0, 0, 36),
